// ðŸ“Œ Este nodo recibe el objeto de error y arma un cuerpo de correo legible
const input = $json;

// FunciÃ³n para formatear cada error
function formatError(item) {
  try {
    const msg = item.message || 'No message';
    const err = item.error || {};
    const errName = err.name || 'UnknownError';
    const level = err.level || 'UnknownLevel';
    const description = err.description || 'No description';
    const nodeName = err.node?.name || 'Unknown Node';
    const timestamp = err.timestamp ? new Date(err.timestamp).toLocaleString() : 'No timestamp';

    return `ðŸš¨ Error capturado:
- Mensaje: ${msg}
- Error: ${errName} (${level})
- DescripciÃ³n: ${description}
- Nodo: ${nodeName}
- Timestamp: ${timestamp}
---------------------------`;
  } catch (e) {
    // Si algo falla, devolvemos el objeto crudo como string
    return `Error al formatear: ${JSON.stringify(item, null, 2)}`;
  }
}

// Aseguramos que siempre sea un array
const errorsArray = Array.isArray(input) ? input : [input];

// Construimos el cuerpo del correo
const emailBody = errorsArray.map(formatError).join('\n\n');

return [
  {
    json: {
      errorData: emailBody
    }
  }
];
