const campoActualizado = $('Loop Over Items').item.json.campo_cambiado;
const valorActualizado = $('Loop Over Items').item.json.valor;

// Mapear el campo cambiado a su propiedad en HubSpot
const mapaCampos = {
  'email_verified_at': 'emailverificado',
  'identity_confirmed_at': 'identidad_validada',
  'phone_verified_at': 'telefono_validado'
};

const propiedadActualizada = mapaCampos[campoActualizado];

// IMPORTANTE: Convertir el valor de la BD (0 o 1) a string 'true' o 'false'
const valorComoString = (valorActualizado == 1 || valorActualizado === true) ? 'true' : 'false';

// Determinar el estado actual de cada validaci√≥n
const validaciones = {
  emailverificado: propiedadActualizada === 'emailverificado' 
    ? valorComoString
    : (userData.emailverificado || 'false'),
  identidad_validada: propiedadActualizada === 'identidad_validada'
    ? valorComoString
    : (userData.identidad_validada || 'false'),
  telefono_validado: propiedadActualizada === 'telefono_validado'
    ? valorComoString
    : (userData.telefono_validado || 'false')
};

// DEBUG: Agregar informaci√≥n para verificar
console.log('Campo actualizado:', campoActualizado);
console.log('Valor recibido de BD:', valorActualizado, 'tipo:', typeof valorActualizado);
console.log('Valor convertido:', valorComoString);
console.log('Validaciones actuales:', validaciones);

// Verificar si TODAS las validaciones est√°n completas (todas deben ser 'true')
const todasVerificadas = 
  validaciones.emailverificado === 'true' &&
  validaciones.identidad_validada === 'true' &&
  validaciones.telefono_validado === 'true';

console.log('¬øTodas verificadas?:', todasVerificadas);

// Retornar el item con la informaci√≥n completa
return [{
  json: {
    ...items[0].json,
    usuario_completamente_verificado: todasVerificadas,
    validaciones_actuales: validaciones,
    usuario_id: userData.hs_object_id || $('GetUser').item.json.results[0].id,
    email: userData.email || $('Loop Over Items').item.json.email_usuario,
    campo_que_completo: todasVerificadas ? campoActualizado : null
  }
}];

const FIELD_MAPPING = {
  'phone': 'mobilephone',
  'first_name': 'firstname',
  'last_name': 'lastname',
  'dni': 'dni',
  'email_verified_at': 'emailverificado',
  'identity_confirmed_at': 'identidad_validada',
  'phone_verified_at': 'telefono_validado'
};

const CAMPOS_VALIDACION = ['email_verified_at', 'identity_confirmed_at', 'phone_verified_at'];
const CAMPOS_NUMERICOS = ['dni'];

const campoDb = $('Loop Over Items').item.json.campo_cambiado;
let valor = $('Loop Over Items').item.json.valor;

if (typeof valor === 'string') valor = valor.replace(/["']/g, '').trim();

const campoHubspot = FIELD_MAPPING[campoDb];
if (!campoHubspot) throw new Error(`Campo no mapeado: ${campoDb}`);

let valorFinal;

if (CAMPOS_VALIDACION.includes(campoDb)) {
  valorFinal = (valor == 1 || valor === true) ? 'true' : 'false';
} else if (CAMPOS_NUMERICOS.includes(campoDb)) {
  valorFinal = Number(valor);
  if (isNaN(valorFinal)) throw new Error(`Valor num√©rico inv√°lido para ${campoDb}: ${valor}`);
} else if (campoDb === 'phone') {
  let limpio = valor.toString().replace(/[\s\-()]/g, '').replace(/[^0-9+]/g, '').replace(/^0+/, '');

  if (!limpio.startsWith('+')) {
    const match = limpio.match(/^(\d{2,4})15(\d{6,8})$/);

    if (match) {
      limpio = `+549${match[1]}${match[2]}`;
    } else if (limpio.startsWith('911')) {
      limpio = '+54' + limpio;
    } else if (limpio.startsWith('11') && limpio.length === 10) {
      limpio = '+549' + limpio;
    } else if (limpio.startsWith('54')) {
      limpio = '+' + limpio;
      if (/^\+54(11|[2-9]\d{1,3})\d{6,8}$/.test(limpio)) {
        limpio = limpio.replace(/^\+54/, '+549');
      }
    } else if (limpio.length > 11) {
      const ultimos10 = limpio.slice(-10);
      if (!ultimos10.startsWith('11')) throw new Error(`N√∫mero con formato inv√°lido: ${limpio}`);
      limpio = '+549' + ultimos10;
    } else if (limpio.length >= 10) {
      limpio = '+549' + limpio;
    } else {
      limpio = '+54' + limpio;
    }
  }

  if (!/^\+[1-9]\d{9,14}$/.test(limpio) || (limpio.startsWith('+54') && (limpio.length < 12 || limpio.length > 14))) {
    throw new Error(`Formato telef√≥nico inv√°lido: ${limpio}`);
  }

  valorFinal = limpio;
} else {
  valorFinal = valor.toString();
}

return [{
  json: {
    contactId: $('GetUser').item.json.results[0]?.id || null,
    properties: { [campoHubspot]: valorFinal },
    campoOriginal: campoDb,
    campoHubspot,
    valor: valorFinal,
    esValidacion: CAMPOS_VALIDACION.includes(campoDb)
  }
}];

